import axios from "axios";
import {useState, useEffect} from 'react';



function PostModal(props)
{
    const visibleClass = 'block ';
    console.log("hi");

    const [post, setPost] = useState({});
    useEffect(() =>{

        axios.get('http://localhost:4000/postthread/' + props.id)
        .then(response => setPost(response.data));

        console.log(post);
    },[]);
    return(
        
        <div className = {"w-screen h-screen absolute top-0 left-0 z-20 flex " + visibleClass} style = {{backgroundColor:'rgba(0,0,0,0.8)'}}>
             <div className = "w-4/5 bg-theme_dark-brighter border border-theme_dark rounded-md bg-white p-5 text-theme_dark  mx-auto self-center align-middle">
                 {post}
             </div>
        </div>
        
    );
}


export default PostModal;
