import TimeAgo from 'timeago-react';
import { ChatAltIcon } from "@heroicons/react/outline";
import AuthButton from './AuthButton';
import CommentForm from './CommentForm';

function Comments(props) {
   const comments = props.comments.filter(comment => props.parentId === comment.parentId);
    function openReplyBox(e)
    {
        e.prevent.default();
        <CommentForm showAuthor = {false}/>
    }
    return (
      <div className = {''}>
          {comments.map(comment => (


            <div className = "mb-2">
                
                <div className = "flex">
                    <div className = {'bg-theme_dark-highlight w-8 h-8 rounded-full my-auto'}/>
                    <div className = {' my-auto py-1 ml-1.5 text-theme_dark-alternateBright'}>
                        Posted by <span className = 'font-bold text-theme_dark'>{comment.author} </span> 
                        <TimeAgo datetime = {comment.postedAt}/>
                    </div>
                </div>

                <div className = "border-l-2 border-theme_dark-highlight mt-1 ml-4 p-3">
                    {comment.body}
                    <div>
                        <button className = "flex" onClick = {(e) => openReplyBox()}>
                            <ChatAltIcon className = "mt-2 h-6 w-6"/> 
                            <span className = "my-1 py-2 text-xs">Reply</span>
                        </button>
                       
                    </div>
                   
                </div>
            </div>

          ))}
          

          
      </div>
    );
  }
  
  export default Comments;
