import {Link} from 'react-router-dom';

function Thread(props){
    console.log(props.open);
    const openClass =  " cursor-pointer hover:border-theme_dark-highlight ";
    const generalClass = " border-2 rounded-md border-theme_dark bg-theme_dark-brighter px-6 py-4 text-theme_dark  ";

    function PostContent()
    {
      return(
        <div>
          <h5 className = "text-theme_dark-alternateBright text-xs">Posted by {props.author}  at {props.postedAt}</h5> 
                  <h2 className = "font-bold text-xl mb-3">{props.title}</h2>
                  <div className = "text-sm leading-6">
                    <p>
                      {props.body}
                    </p>
                  </div>
        </div>
      );
    }
    return (
        
            <div className = "bg-theme_dark-brighter px-6 py-4 text-theme_dark-brighter">
              
              {/*When post is displayed in subforum page */}
              {!props.open  && (

                <div  className = { generalClass + openClass }>
                     
                     <Link to={{pathname:'/posts/'+props._id,state:{postId:props._id}}} className={postClasses}>
                    <PostContent/>                
                                        
                  </Link>
                </div>
              )}


               {/*When post is opened seperately */}
              {props.open === true && (
                <div className = {generalClass}>
                  <PostContent/>
                </div>
              )
                
              }
              
            </div>
       
    );
}
export default Thread;
