//For rendering the contents of a post in a modal pop up


import axios from "axios";
import {useState, useEffect} from 'react';
import PostContent from "./PostContent";
import ClickOutHandler from "react-clickout-handler";


function PostModal(props)
{
    
    console.log("hi");

    const [post, setPost] = useState({});
    const visibleClass = props.open ? 'block' : 'hidden';


    useEffect(() => {

        axios.get('http://localhost:4000/postthread/' + props.id)
        .then(response => {
            setPost(response.data)
            
            
        });
       
        
    },[props.id]);


    function close(){
        setPost({});
        props.onClickOut();
    }
    return(
        
        <div className = {"w-screen h-screen fixed top-0 bottom-0 left-0 z-20 flex " + visibleClass} style = {{backgroundColor:'rgba(0,0,0,0.8)'}}>
       
       <ClickOutHandler onClickOut = {() => {close()}}>
             <div className = "w-4/5 bg-theme_dark-brighter  border border-theme_dark rounded-md bg-white p-3  text-theme_dark  mx-auto self-center align-middle">
                 <div className = "p-2 border-2 border-theme_dark-highlight rounded-md">
                
                   
                    <div className = "block overflow-scroll" style = {{maxHeight : "calc(100vh - 10rem)"}}>
                     
                        <PostContent open = {true} {...post}/>
                    
                    </div>
                
                 </div>
                 
                 
             </div>
        </ClickOutHandler>
        </div>
        
        
    );
}


export default PostModal;
