import {useContext, useState} from "react";
import ThreadTextEditor from "./ThreadTextEditor";
import UserContext from "./UserContext"
import AuthButton from "./AuthButton";
import axios from "axios";
import AuthModalContext from "./AuthModalContext";


function CommentForm(props){
    const user = useContext(UserContext);
    const authModalContext = useContext(AuthModalContext);

    const [CommentBody, setCommentBody] = useState('');

    function postComment(e){
        e.preventDefault();
        const data = {body:CommentBody, parentId:props.parentId, rootId:props.rootId};
        axios.post("http://localhost:4000/postcomment", data,{withCredentials: true} )
        .then(response => {
            setCommentBody('');
            if(props.onSubmit){
                props.onSubmit();
            }
        }).catch(error => {
            if(error.response.status === 401){
                authModalContext.setShow('login');
            }
        });
    }
    return(
    <div>
        {
            props.showAuthor && (
                <p className = {'text-theme_dark-alternateBright text-sm mb-2'}> <span class = "text-theme_dark font-bold text-sm">Comment as : </span>{user.username}</p>
            )
        }
        
        <form onSubmit = {(e) => postComment(e)}>
            
            <input 
                onChange = {e => setCommentBody(e.target.value)}
                value = {CommentBody}
                placeholder = {'Comment'}
                className = {"bg-theme_border"}/>
            <div className = {'text-right'}>
              
                {!!props.onCancel && (
                    <AuthButton outline 
                                className = "mr-1"
                                onClick = {e => props.onCancel()}
                    >
                        <span className = "text-theme_dark">    Cancel </span>
                    </AuthButton>
                )

                }
                <AuthButton 
                   className = "mb-1 mr-1">
                    Comment
                </AuthButton>
            </div>
        </form>
    </div>);
}

export default CommentForm;
