//CommentPage.js
import { useParams } from 'react-router-dom';

import {useState, useEffect} from 'react';

import axios from 'axios';
import Thread from './Thread';
import Comment from './Comment';
import AboutAuthor from './AboutAuthor';
function ThreadPage(){
    const {id} = useParams(); //The thread id
    //console.log(id);
    const [post, setPost] = useState({});
    useEffect(() =>{

        axios.get('http://localhost:4000/postthread/' + id)
        .then(response => setPost(response.data));

        console.log(post);
    },[]);

    
    // w-screen h-screen relative fixed top-0 bottom-0 left-0
    return(
        <>
        <div className = "bg-theme_dark-brighter w-screen relative fixed top-0 bottom-0 left-0 p-11">
           {/* {post && (
               <Thread {...post} open={true}/>
                   
           )} */}
           <div className = "flex">

                
                <div className = " bg-theme_dark-brighter flex-1">
                     <Comment id = {id}/>
                </div>
                <div className = "max-w-xl">
                    <AboutAuthor/>
                </div>
           </div>
           
        </div>
        {/* <div className = "bg-theme_dark-brighter w-screen h-screen bottom-0 left-0 p-11"></div> */}
        </>
    );
}

export default ThreadPage;
