import UserContext from "./UserContext";
import { useState, useContext, useEffect } from "react";

import AuthInput from "./AuthInput";
import AuthButton from "./AuthButton";
import axios from "axios";
import { Link } from "react-router-dom";
import FemaleAvatar from './female_avatar.svg';
function UserProfile(props)
{
    const user = useContext(UserContext);
    const username = props.match.params.username; 
   console.log('username is ' + username);
    const [email, setEmail] = useState(user.email);
    const [name, setName] = useState(user.name);
   
    const [school, setSchool] = useState(user.school);
    const [department, setDepartment] = useState(user.department);
    const [biography, setBiography] = useState(user.biography);
    const profileUser = user.username;
    console.log('profileuser is ' + profileUser);



    useEffect(() => {
       
        
        axios.get('http://localhost:4000/profile', {withCredentials:true})
             .then((response) => {
                console.log(response.data);
                setBiography(response.biography);
                setDepartment(response.department);
                setSchool(response.school);
                setEmail(response.email);
                setName(response.name);
              });
    
      }, []);



 



    return(
    <div className = {'bg-theme_dark-alternateBright w-screen h-screen '}>


        {username === profileUser && 
        (
        <div className = " p-4 text-theme_border opacity-70">
            Welcome to your profile {username}!
            
           
                <div className = "border border-theme_dark rounded-md px-10 mx-7 shadow-md">
                <h1 className = "text-theme_dark-highlight mt-2 text-2xl font-bold text-center mb-2">{username}</h1>
                <div className = "flex justify-center">
                    <img src = {FemaleAvatar} alt = "" className = "w-2/5 h-2/5 "/>
                </div>
                <div className = "my-3 p-3 border-t-2 ">
                    <p className = "text-sm leading-6 text-white opacity-70">Name : {name}</p>
                    <p className = "text-sm leading-6 text-white opacity-70">Email : {email}</p>
                    <p className = "text-sm leading-6 text-white opacity-70">School : {school}</p>
                    <p className = "text-sm leading-6 text-white opacity-70">Department : {department}</p>
                    <p className = "text-sm leading-6 text-white opacity-70 italic"> {biography}</p>
                </div>
           
            
                <Link>
                <AuthButton solid className = "w-1/2 mx-auto my-2">Update Profile</AuthButton>
                </Link>
            </div>
            
        </div>
        )}
        {
            !profileUser && (
                <div>
                    Please log in to edit your profile
                </div>
            )
        }
        {
            profileUser && username !== profileUser && (
                <div>
                    <p>Oops! {profileUser} is not allowed to access profile of {username}</p>.
                </div>
            )
        }
       
    </div>);
}

export default UserProfile;
