import UserContext from "./UserContext";
import { useState, useContext } from "react";

import AuthInput from "./AuthInput";
import AuthButton from "./AuthButton";
import axios from "axios";
import SavedPostsListing from "./SavedPostsListing";
import notAllowed from './notAllowed.svg';

function SavedPosts(props)
{
    const user = useContext(UserContext);
    const username = props.match.params.username; //the user whose bookmarks we are trying to fetch
    const profileUser = user.username; //the user who is logged in
    return(
        <div>
             {username === profileUser && 
            (
            <div className = " text-center p-4 bg-theme_dark-alternateBright w-full h-full">
                
                
                <div className = "text-theme_border font-bold text-3xl my-3">Your saved posts are here <span className = "text-theme_dark-highlight">{username}!</span> </div>
                
                <div className = "flex">
                    <div className = "flex-1">
                    <SavedPostsListing username = {username}/>
                    </div>
                   
                </div>
                  

           </div>
   
            )}
            {
                !profileUser && (
                    <div>
                        Please log in to view your saved posts.
                    </div>
                )
            }
            {
                profileUser && username !== profileUser && (
                    <div className = "bg-theme_dark-alternateBright w-full h-full">
                        <p className = "text-theme_border font-bold text-3xl my-3">Oops! {profileUser} is not allowed to access profile of {username}</p>.
                        <img src = {notAllowed} alt = "" />
                    </div>
                )
            }
           
            
        </div>
    );
}

export default SavedPosts;
