//CommentPage.js
import { useParams } from 'react-router-dom';

import {useState, useEffect} from 'react';

import axios from 'axios';
import Thread from './Thread';
import Comment from './Comment';
function ThreadPage(){
    const {id} = useParams(); //The thread id
    //console.log(id);
    const [post, setPost] = useState({});
    useEffect(() =>{

        axios.get('http://localhost:4000/postthread/' + id)
        .then(response => setPost(response.data));

        console.log(post);
    },[]);

    
    // w-screen h-screen relative fixed top-0 bottom-0 left-0
    return(
        <div className = "bg-theme_dark-brighter w-screen h-screen relative fixed top-0 bottom-0 left-0 p-11">
           {/* {post && (
               <Thread {...post} open={true}/>
                   
           )} */}
           <div className = " bg-theme_dark-brighter w-screen h-screen relative fixed top-0 bottom-0 left-0">
                <Comment id = {id}/>
           </div>
           
           
        </div>
    );
}

export default ThreadPage;
