import UserContext from "./UserContext";
import { useState, useContext, useEffect } from "react";

import AuthInput from "./AuthInput";
import AuthButton from "./AuthButton";
import axios from "axios";

function UserProfile(props)
{
    const user = useContext(UserContext);
    const username = props.match.params.username; 
   console.log('username is ' + username);
    const [email, setEmail] = useState(user.email);
    const [name, setName] = useState(user.name);
   
    const [school, setSchool] = useState(user.school);
    const [department, setDepartment] = useState(user.department);
    const [biography, setBiography] = useState(user.biography);
    const profileUser = user.username;
    console.log('profileuser is ' + profileUser);



    useEffect(() => {
        const username = props.username;
        
        axios.get('http://localhost:4000/profile', {withCredentials:true})
             .then((response) => {
                console.log(response.data);
              });
    
      }, []);
    function updateProfile(e){
        e.preventDefault();
        console.log('updated ' + name);
        console.log('updated ' + email);
        console.log('updated ' + school);
        console.log('updated ' + department);
        console.log('updated ' + biography);
        const data = {name, email, school, department,biography, username};
        axios.put('http://localhost:4000/update/', data, {withCredentials:true})
        .then(() => {
            user.setUser({username});
            user.setName({name});
            user.setEmail({email});
            user.setSchool({school});
            user.setDepartment({department});
            user.setBiography({biography});
          });

    }
    return(
    <div className = {'bg-theme_dark-alternateBright w-screen h-screen '}>


        {username === profileUser && 
        (
        <div className = " p-4 text-theme_border opacity-70">
            Welcome to your profile {username}!
            <div className = "bg-theme_dark-alternateBright border-2 border-theme_dark rounded-md bg-white w-1/2 shadow-md p-4">
                <label className="mb-5"> 
                    Name
                    <AuthInput type = "text" 
                               className = "mb-2 w-1/2  " 
                               value = {name} 
                               onChange = {e => setName(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    Email
                    <AuthInput type = "email" 
                               className = "mb-2 w-1/2  " 
                               value = {email} 
                               onChange = {e => setEmail(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    School
                    <AuthInput type = "school" 
                               className = "mb-2 w-1/2  " 
                               value = {school} 
                               onChange = {e => setSchool(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    Department
                    <AuthInput type = "text" 
                               className = "mb-2 w-1/2  " 
                               value = {department} 
                               onChange = {e => setDepartment(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    Your Bio
                    <AuthInput type = "text" 
                               className = "mb-2 w-1/2  " 
                               value = {department} 
                               onChange = {e => setBiography(e.target.value)} />
                </label>

                <AuthButton solid className = "w-1/2 mx-auto my-2" onClick = {e => updateProfile(e)}>Save</AuthButton>
            </div>
        </div>
        )}
        {
            !profileUser && (
                <div>
                    Please log in to edit your profile
                </div>
            )
        }
        {
            profileUser && username !== profileUser && (
                <div>
                    <p>Oops! {profileUser} is not allowed to access profile of {username}</p>.
                </div>
            )
        }
       
    </div>);
}

export default UserProfile;
