import { ArrowCircleDownIcon, ArrowCircleUpIcon } from "@heroicons/react/solid";
import axios from "axios";
import {useContext} from 'react';
import RootCommentContext from "./RootCommentContext";

function Voting(props)
{

    const rootCommentInfo = useContext(RootCommentContext);
    const buttonClassName =  "mt-2 mx-1 h-6 w-6 bg-theme_dark text-theme_dark-brighter rounded-full hover:text-theme_dark-highlight";


    function sendVote(direction = 'up'){
        const url = 'http://localhost:4000/vote/' + props.postId + '/' + direction;
        axios.get(url, {withCredentials:true})
            .then(response => {
                console.log(response.data);
            });
    }
    function handleVoteUp(){
        sendVote('up');
    }
    function handleVoteDown(){
        sendVote('down');
    }



    const total = rootCommentInfo.commentsTotals[props.postId] || 0;



    return(
    <div className = {'inline flex ' }>
       
        {/* Upvote */}
        <ArrowCircleUpIcon className ={buttonClassName}
                            onClick = {() => handleVoteUp()}/>

        {/* Number of Votes */}
        <span className = {'my-1 py-2 text-xs font-bold'}>{total}</span>


        {/* DownVote */}
        <ArrowCircleDownIcon className = {buttonClassName}
                            onClick = {() => handleVoteDown()}/>
    </div>
    );
}

export default Voting;
