import SubForum from './SubForum'; //Board
import ThreadPage from './ThreadPage'; //CommentPage
import {useState, useEffect} from "react";
import {
    Switch, 
    Route,
    useLocation,
    
  } from 'react-router-dom';
import PostModal from './PostModal';
  


function RoutingSwitch(){
    let location = useLocation();
    const [postOpen, setPostOpen] = useState(false);
    console.log(location);

    
    let postId = null;
    if(location.state && location.state.postId){
        location.pathname = '/';
        postId = location.state.postId;
    }
    

    // if (location.state && location.state.postId) {
    //     location.pathname = '/';
    //     if (postOpen) {
    //     postId = location.state.postId;
    //     } else {
    //     location.state.postId = null;
    //     }

    // }


    // useEffect(() => {
    //     setPostOpen(true);
    //   }, [postId]);
    
    //   useEffect(() => {
    //     postId = null;
    //   }, [postOpen]);


    return(

        <div>
            {postId && (
                    <div>

                        <PostModal id = {postId}/>
                    </div>
                )}
            <Switch location = {location}>
                
                <Route exact path = "/" element = {<SubForum/>}/> 
                <Route exact path = {"/posts/:id"} element = {<ThreadPage/>}/> 
            </Switch>
        </div>
    );
}

export default RoutingSwitch;
