import UserContext from "./UserContext";
import { useState, useContext } from "react";

import AuthInput from "./AuthInput";
import AuthButton from "./AuthButton";
import axios from "axios";
function UserProfile()
{
    const user = useContext(UserContext);


    const [email, setEmail] = useState(user.email);
    const [name, setName] = useState(user.name);
    const [username, setUsername] = useState(user.username);
    const [school, setSchool] = useState(user.school);
    const [department, setDepartment] = useState(user.department);

    function updateProfile(e){
        e.preventDefault();
        console.log('updated ' + name);
        console.log('updated ' + email);
        console.log('updated ' + school);
        console.log('updated ' + department);
        const data = {name, email, school, department};
        axios.post('http://localhost:4000/update', data, {withCredentials:true})
        .then(() => {
            // user.setUser({username});
            // user.setEmail({email});
            // user.setSchool({school});
            // user.setDepartment({department});
            
          });

    }
    return(
    <div className = {'bg-theme_dark-brighter w-screen h-screen '}>
        <div className = " text-center p-4">
            Welcome to your profile {user.username}!
            <div className = "text-center border border-theme_dark rounded-md bg-white w-1/2 mx-auto">
                <label className="mb-5"> 
                    Name
                    <AuthInput type = "text" 
                               className = "mb-2 w-1/2 mx-auto " 
                               value = {name} 
                               onChange = {e => setName(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    Email
                    <AuthInput type = "email" 
                               className = "mb-2 w-1/2 mx-auto " 
                               value = {email} 
                               onChange = {e => setEmail(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    School
                    <AuthInput type = "school" 
                               className = "mb-2 w-1/2 mx-auto " 
                               value = {school} 
                               onChange = {e => setSchool(e.target.value)} />
                </label>
                <label className="mb-5"> 
                    Department
                    <AuthInput type = "email" 
                               className = "mb-2 w-1/2 mx-auto " 
                               value = {department} 
                               onChange = {e => setDepartment(e.target.value)} />
                </label>
                <AuthButton solid className = "w-1/2 mx-auto my-2" onClick = {e => updateProfile(e)}>Save</AuthButton>
            </div>
        </div>
    </div>);
}

export default UserProfile;
