import {useContext, useState} from "react";
import ThreadTextEditor from "./ThreadTextEditor";
import UserContext from "./UserContext"
import AuthButton from "./AuthButton";
import axios from "axios";



function CommentForm(props){
    const user = useContext(UserContext);
    const [CommentBody, setCommentBody] = useState('');

    function postComment(){
        const data = {CommentBody, parentId:props.parentId, rootId:props.rootId};
        axios.post("http://localhost:4000/postthread", data,{withCredentials: true} )
        .then(response => {

        });
    }
    return(
    <div>
        <p className = {'text-theme_dark-alternateBright text-sm mb-2'}> <span class = "text-theme_dark font-bold text-sm">Comment as : </span>{user.username}</p>
        <form onSubmit = {(e) => postComment(e)}>
            
            <ThreadTextEditor 
                onChange = {e => setCommentBody(e.target.value)}
                value = {CommentBody}
                placeholder = {'Comment'}/>
            <div className = {'text-right'}>
              
                
                <AuthButton 
                   className = "mb-1 mr-1">
                    Comment
                </AuthButton>
            </div>
        </form>
    </div>);
}

export default CommentForm;
