function App() {
  const [showAuthModal,setShowAuthModal] = useState(false);
  const [user, setUser] = useState({});
  const [showCreateThreadModal, setShowCreateThreadModal] = useState(false);
  // const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get('https://chai-stop-server.herokuapp.com/user', {withCredentials:true})
    .then(response => setUser(response.data));

    // axios.get('https://chai-stop-server.herokuapp.com/postthread', {withCredentials:true})
    // .then(response => setPosts(response.data));

  }, []);



  function logout(){
    axios.post('https://chai-stop-server.herokuapp.com/logout', {}, {withCredentials: true})
    .then(() =>{
          setUser({});
          <Redirect to = "/"/>       

        });
  }





  return (
    <div className = "">
    <AuthModalContext.Provider value = {{show: showAuthModal, setShow: setShowAuthModal}}>
     <CreateThreadModalContext.Provider value = {{show: showCreateThreadModal, setShow: setShowCreateThreadModal}}>
      <UserContext.Provider value = {{...user, logout, setUser}}>  

        {/* <Router>
        <Header/>  
          <Routes>
            <Route exact path = "/" element = {<SubForum/>}/> 
            <Route exact path = {"/posts/:id"} element = {<ThreadPage/>}/> 
          </Routes>
        </Router>  */}
        <Routing/>


        {/* <PostsListing/> */}







      </UserContext.Provider>
      </CreateThreadModalContext.Provider>
    </AuthModalContext.Provider>

    </div>
  );
}

export default App;
