//Modal To create threads in a subforum

import ClickOutHandler from "react-clickout-handler";

function CreateThreadModal(){
    const visibleClass = 'block';
    return(
        <div>
             <div className = {"w-screen h-screen absolute top-0 left-0 z-20 flex " + visibleClass} style = {{backgroundColor:'rgba(0,0,0,0.8)'}}>
             <ClickOutHandler onClickOut={() => {}}>
             {/* <ClickOutHandler onClickOut={() => {modalContext.setShow(false)}}> */}

           
                    <div className = "w-4/5 md:w-2/4  border border-theme_dark rounded-md bg-white p-5 text-theme_dark  mx-auto self-center align-middle">
                        <input className = {"border-2  w-full border-theme_dark-brighter rounded-md p-2 my-2 block " }/>

                        <textarea className = {"border-2 w-full border-theme_dark-brighter rounded-md p-2 my-2 block " }/>
                    
                    </div>
                </ClickOutHandler>
             </div>
        </div>
    );
}

export default CreateThreadModal;
